document.addEventListener("alpine:init",()=>{Alpine.data("todo",()=>({todoList:"",NewContentTodo:"",NewDateTodo:"",getTodo(){fetch("https://todo.localhost/api/getTodo",{method:"GET"}).then(o=>o.json()).then(o=>this.todoList=o).catch(o=>console.log(o))},addTodo(){fetch("https://todo.localhost/api/addTodo",{method:"POST",headers:{"content-type":"application/json"},body:JSON.stringify({content:this.NewContentTodo,date:this.NewDateTodo})}).then(()=>{this.NewContentTodo="",this.NewDateTodo=""}).then(()=>{this.getTodo()}).catch(o=>console.log(o))},removeTodo(o){fetch("https://todo.localhost/api/removeTodo",{method:"POST",headers:{"content-type":"application/json"},body:JSON.stringify({id:o})}).then(()=>{this.getTodo()}).catch(o=>console.log(o))},modifyTodo(o,t,e,n){fetch("https://todo.localhost/api/modifyTodo",{method:"POST",headers:{"content-type":"application/json"},body:JSON.stringify({id:o,content:t,date:e,done:n})}).then(()=>{this.getTodo()}).catch(o=>console.log(o))},doneTodo(o,t){fetch("https://todo.localhost/api/doneTodo",{method:"POST",headers:{"content-type":"application/json"},body:JSON.stringify({id:o,done:t})}).then(()=>{this.getTodo()}).catch(o=>console.log(o))}})),Alpine.data("modify",()=>({showModify:!1,newContent:"",newDate:"",newDone:"",modifyCurrent(o){""!=this.NewDate&&""!=this.newContent&&(this.modifyTodo(o,this.newContent,this.newDate,this.newDone),this.showModify=!1)},initDone(o,t){this.newDone=o,t.checked="1"==o},changeDone(o,t){this.newDone="1"==this.newDone?"0":"1",t.checked="1"==this.newDone,this.doneTodo(o,this.newDone)}}))});